// PaymentPage.js
import React, { useState } from 'react';
import axios from 'axios';
import StripeCheckout from 'react-stripe-checkout';
import './App.css';

const PaymentPage = () => {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState('');

  const handleToken = async (token) => {
    try {
      const response = await axios.post(' http://localhost:3000/api/transactions', {
        name,
        amount,
        transactionID: token.id
      });
      console.log(response.data.message);
      // You can redirect the user or show a success message here
    } catch (error) {
      console.error('Error:', error.response.data.error);
    }
  };

  return (
    <div className="payment-page">
      <h1>Make a Payment</h1>
      <div className="payment-form">
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="number" placeholder="Amount ($)" value={amount} onChange={(e) => setAmount(e.target.value)} />
        <StripeCheckout
          token={handleToken}
          stripeKey="pk_test_51PHSs2SCYZ0If31BnY1wMhBKNOBfSLMgV6oTOX5f8WAhEgc6wanPznDLF7MgrnSntkXW37nUCjUfn3TVV2e2s2kN00bbOsLmC7"
          amount={Number(amount) * 100} // Stripe takes amount in cents
          currency="USD"
        >
          <button className="pay-btn">Pay Now</button>
        </StripeCheckout>
      </div>
    </div>
  );
};

export default PaymentPage;
